import React, { useEffect, useState } from "react";

let playersList = [
  {
    name: "player A",
    gender: "Male",
    password: "123",
    email: "aaaa@b.com",
    team: "Team A",
    isCaptain: 'no'
  },
  {
    name: "player B",
    password: "123",
    gender: "Male",
    email: "bbbb@b.com",
    team: "Team A",
    isCaptain: 'no'
  }, 
  {
    name: "player C",
    gender: "Male",
    password: "123",
    email: "ccccc@b.com",
    team: "Team B",
    isCaptain: 'no'
  }, 
  {
    name: "player D",
    gender: "FeMale",
    password: "123",
    email: "ddddd@b.com",
    team: "B",
    isCaptain: 'yes'
  }
];

const TableContext = React.createContext({
  //   token: '',
  playersList: playersList,
  addPlayers: (data) => {}
});

export const TableContextProvider = (props) => { 
    const [listtLength, setListLength] = useState(4);

    // useEffect(() => {
    //     setListLength((prevListLengthState) => {
    //         if (prevListLengthState < listtLength) {
    //             return listtLength;
    //         }
    //     })

    // }, [])
    const addPlayersHandler = (playerdata) => {
        if (playerdata.team === '') {
            playerdata.team = 'Yet to select'
        }
      playersList = [...playersList, playerdata];
      setListLength(playersList.length)
      console.log('new player', playersList) 
    }

    const contextVal = {
        playersList: playersList,
        addPlayers: addPlayersHandler
        }

    return (
        <TableContext.Provider value={contextVal}>
          {props.children}
        </TableContext.Provider>
      );
}

export default TableContext;
