import { useState, useRef, useContext } from "react";
import { useHistory } from "react-router-dom";
import AuthContext from "../../store/AuthContext";
import TableContext from "../../store/TableContext";

// import AuthContext from '../../store/auth-context';
import classes from "./Auth.module.css";

const Auth = () => {
  const tableCtx = useContext(TableContext);
  const history = useHistory();
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const fnameInputRef = useRef();
  const lnameInputRef = useRef();
  //   const femaleInputRef = useRef();

  let genderValidation;
  let enteredEmail;
  let enteredPassword;
  let enteredFname;
  let enteredLname;
  //   const authCtx = useContext(AuthContext);

  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [gender, setGender] = useState('male');
  const [isCaptain, setIsCaptain] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const genderChange = (e) => {
    setGender(e.target.value);
  }

  const captainChange = () => {
    setIsCaptain((prevState) => !prevState);
    console.log(isCaptain)
  }

  const submitHandler = (event) => {
    event.preventDefault();
    if (!isLogin) {
       enteredFname = fnameInputRef.current.value;
       enteredLname = lnameInputRef.current.value;
    }
    enteredEmail = emailInputRef.current.value;
    enteredPassword = passwordInputRef.current.value; 

    console.log(isCaptain);
    const playerData = {
        name: enteredFname + enteredLname,
        email: enteredEmail,
        gender: gender,
        isCaptain: isCaptain,
        team: '',
        password: enteredPassword,
    }
    tableCtx.addPlayers(playerData);
    history.push("/playersList");
    setIsLoading(false);
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? "Login" : "Sign Up"}</h1>
      <form onSubmit={submitHandler}>
        {!isLogin && (
          <div className={classes.fname}>
            <div className={classes.control + " col-md-6"}>
              <label htmlFor="fname"> First Name</label>
              <input type="fname" id="fname" required ref={fnameInputRef} />
            </div>
            <div className={classes.control + " col-md-6"}>
              <label htmlFor="lname"> Last Name</label>
              <input type="lname" id="lname" required ref={lnameInputRef} />
            </div>
          </div>
        )}
        <div className={classes.control}>
          <label htmlFor="email"> Email</label>
          <input type="email" id="email" required ref={emailInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="password"> Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>

        {!isLogin && (
          <div className={classes.gender}>
            <div className={classes.align + " col-xs-6"}>
              <label htmlFor="gender">Gender:</label>
              <div className={classes.radiobtn}>
                <div>
                  <input
                    id="male"
                    type="radio"
                    value="male"
                    name="gender"
                    required
                    onChange={genderChange}
                  />
                  <label htmlFor="male">Male</label>
                </div>

                <div className={classes.f}>
                  <input
                    id="female"
                    type="radio"
                    value="female"
                    name="gender"
                    required
                    className={classes.fgender}
                    onChange={genderChange}
                  />
                  <label htmlFor="female">Female</label>
                </div>
              </div>
              {genderValidation && (
                <div class="alert alert-danger">
                  <div>Name is required.</div>
                </div>
              )}
            </div>
            <div className="col-xs-6  form-group form-check">
              <input
                type="checkbox"
                id="isCaptain"
                className="form-check-input"
                onChange={captainChange}
                value={isCaptain}
              />
              <label
                htmlFor="isCaptain"
                className={classes.checkbox + " form-check-label"}
              >
                isCaptain
              </label>
            </div>
          </div>
        )}
        <div className={classes.actions}>
          {!isLoading && (
            <button>{isLogin ? "Login" : "Create Account"}</button>
          )}
          {isLoading && <p>Sending request...</p>}
          <button
            type="button"
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? "Create new account" : "Login with existing account"}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Auth;
