import classes from "./FilteredTeams.module.css";

const FilteredTeams = (props) => {
  const players = props.players;
  const handleClick = (player, id) => {
    props.onSelect(player);
    document.getElementById(id).style.border = "solid #008080";
  }
  const cards = players.map((player, id) => {
    return (
      <div className={classes.child} key={id}>
        <div className={classes.card + " mb-3"} style={{ maxWidth: "540px" }} id={id} onClick={() => handleClick(player, id)}>
          <div className={classes.row + " no-gutters"}>
            <div className={classes.avatar + " col-xs-12"}>
              <img
                src={player.img}
                className={classes.cardimg + " card-img"}
                alt="..."
              />
            </div>
            <div className={classes.cardbody + " col-xs-12"}>
              <div className="card-body">
                <h5 className="card-title">{player.name}</h5>
                <p className="card-text">{player.gender}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  });
  return <div className={classes.parent}>{cards}</div>;
};

export default FilteredTeams;
