import React, { useEffect, useState } from "react";

let playersList = [
  {
    name: "player A",
    Gender: "Male",
    password: "123",
    email: "aaaa@b.com",
    team: "A",
    isCaptain: 'no'
  },
  {
    name: "player B",
    password: "123",
    Gender: "Male",
    email: "bbbb@b.com",
    team: "A",
    isCaptain: 'no'
  }, 
  {
    name: "player C",
    Gender: "Male",
    password: "123",
    email: "ccccc@b.com",
    team: "B",
    isCaptain: 'no'
  }, 
  {
    name: "player D",
    Gender: "FeMale",
    password: "123",
    email: "ddddd@b.com",
    team: "B",

  }
];

const TableContext = React.createContext({
  //   token: '',
  playersList: playersList,
  addPlayers: (data) => {}
});

export const TableContextProvider = (props) => { 
    const [listtLength, setListLength] = useState(4);

    useEffect(() => {
        setListLength((prevListLengthState) => {
            if (prevListLengthState < listtLength) {
                return listtLength;
            }
        })

    }, [listtLength])
    const addPlayersHandler = (playerdata) => {
      playersList.push(playerdata);
      setListLength(playersList.length)
      console.log('new player', playersList) 
    }

    const contextVal = {
        playersList: playersList,
        addPlayers: addPlayersHandler()
    }

    return (
        <TableContext.Provider value={contextVal}>
          {props.children}
        </TableContext.Provider>
      );
}

export default TableContext;
