import { Redirect, Route, Switch } from "react-router-dom";
import Layout from './component/layout/Layout'
import Match from "./pages/Match";
import NotFound from "./pages/NotFound";
import PlayerList from "./pages/PlayerList";
import SelectTeam from "./pages/SelectTeam";
import React, { useContext } from "react";
import Auth from "./component/auth/Auth";
import AuthContext from "./store/AuthContext";

function App() {
  const authCtx = useContext(AuthContext);

  return (
    <Layout>
      {/* <Suspense
        fallback={
          <div className="centered">
            <LoadingSpinner />
          </div>
        }
      > */}
        <Switch>
          <Route path="/" exact>
            <Redirect to="/login" />
          </Route>
          {!authCtx.isLoggedIn && <Route path="/login" exact>
            <Auth />
          </Route>}
         { authCtx.isLoggedIn  && <Route path="/logout" exact>
            <Auth />
          </Route> }
          { authCtx.isLoggedIn  && <Route path="/playersList">
            <PlayerList />
          </Route>}
          { authCtx.isLoggedIn  && <Route path="/matchDetails">
            <Match />
          </Route>}
          { authCtx.isLoggedIn  && <Route path="/selectTeam">
            <SelectTeam />
          </Route> }
          <Route path="/not-found">
            <NotFound />
          </Route>
          <Route path="*">
            <Redirect to="/not-found" />
          </Route>
        </Switch>
      {/* </Suspense> */}
    </Layout>
  );
}

export default App;
