import React, { useState, useContext } from "react";
import TableContext from "./TableContext";


const AuthContext = React.createContext({
  //   token: '',
  isLoggedIn: () => {},
  isAuth: false,
  inSignup: false,
  isSignUp: () => {},
  login: () => {},
  logout: () => {},
});


export const AuthContextProvider = (props) => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [isAuth, setIsAuth] = useState(false);
  const tableCtx = useContext(TableContext);
  const playersList = tableCtx.playersList;


  const logoutHandler = () => {
    // setIsLogin(false);
    setIsAuth(false);
    localStorage.removeItem('token');
  };

  const loginHandler = (data, loginState) => {
    let noError = false;
    // console.log(loginState)
    if (loginState) {
        playersList.map((players) => {
            if (players.email === data.email && players.password === data.password) {
                setIsLogin(true);
                window.localStorage.setItem('token', players.id)
                noError = true
            }
        })
        if (!noError) {
                alert('please provide valid email and password');      
              }
    } else {
      window.localStorage.setItem('token', data.id)
        setIsLogin(true);
    }
  };

  const isSignUpHandler = () => {
     setIsSignUp((prevState) => !prevState);
  }

  const contextValue = {
    isLoggedIn: isLoggedInHandler,
    inSignup: isSignUp,
    isSignUp: isSignUpHandler,
    login: loginHandler,
    logout: logoutHandler,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
