  //   const [token, setToken] = useState(initialToken);

  //   const userIsLoggedIn = !!token;
  const [isLogin, setIsLogin] = useState(false);
  const tableCtx = useContext(TableContext);
  const history = useHistory();

  const logoutHandler = () => {
    //    useCallback(() => {
    //     setToken(null);
