    console.log(loginState)
    if (loginState) {
        playersList.map((players) => {
            if (players.email === data.email && players.password === players.password) {
                history.push('/playersList')
            } else {
                alert('please provide valid email and password')
            }
        })
    } else {
        tableCtx.addPlayers(data);
        history.push('/playersList')
    }
    setIsLogin(true);
  };

  //   useEffect(() => {
